    /*
     * Get livre by liv_id
     */
    function get_livre($liv_id)
    {
        return $this->db->get_where('livres',array('liv_id'=>$liv_id))->row_array();
    }
    
    /*
     * Get all livres count
     */
    function get_all_livres_count()
    {
        $this->db->from('livres');
        return $this->db->count_all_results();
    }
        
    /*
     * Get all livres
     */
    function get_all_livres($params = array())
    {
        $this->db->order_by('liv_id', 'desc');
        if(isset($params) && !empty($params))
        {
            $this->db->limit($params['limit'], $params['offset']);
        }
        return $this->db->get('livres')->result_array();
    }
        
