     * function to add new livre
     */
    function add_livre($params)
    {
        $this->db->insert('livres',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update livre
     */
    function update_livre($liv_id,$params)
    {
        $this->db->where('liv_id',$liv_id);
        return $this->db->update('livres',$params);
    }
    
    /*
     * function to delete livre
     */
    function delete_livre($liv_id)
    {
        return $this->db->delete('livres',array('liv_id'=>$liv_id));
    }

    public function get_catalogue()
    {
    
        $this->db->order_by('livres.liv_id', 'DESC');
        $this->db->join('ouvrages', 'ouvrages.ouv_id = livres.ouvrage_sid');
        $this->db->join('auteurs', 'auteurs.aut_id = ouvrages.auteur_sid');

        $query = $this->db->get('livres');

        if ($query) {
            return $query->result();
        } else {
            return false;
        }
        
    }
